mapper.delete:
 DELETE FROM UserInfo
 WHERE 1=1 

	 and username= #{username}


	 and password= #{password}


	 and realname= #{realname}


	 and phone= #{phone}


	 and address= #{address}


	 and sex= #{sex}


	 and role_id= #{roleId}


	 and id= #{id}

mapper.select:
 SELECT 
	 id,
	 username AS username,
	 password AS password,
	 realname AS realname,
	 phone AS phone,
	 address AS address,
	 sex AS sex,
	 role_id AS roleId
FROM UserInfo
WHERE 1=1 

	 and username= #{username}


	 and password= #{password}


	 and realname= #{realname}


	 and phone= #{phone}


	 and address= #{address}


	 and sex= #{sex}


	 and role_id= #{roleId}


	 and id= #{id}
