mapper.select:
SELECT id, order_date AS orderDate, customer AS customer, money AS money, pay_type AS payType, real_money AS realMoney, order_type AS orderType, user_id AS userId FROM ls_order WHERE 1=1
and customer LIKE CONCAT(CONCAT('%', #{customer}),'%')
limit #{pageSize} offset #{offset}

mapper.delete:
 DELETE FROM ls_order
 WHERE 1=1 

	 and order_date= #{orderDate}


	 and customer= #{customer}


	 and money= #{money}


	 and pay_type= #{payType}


	 and real_money= #{realMoney}


	 and order_type= #{orderType}


	 and user_id= #{userId}


	 and id= #{id}

mapper.select:
 SELECT 
	 id,
	 order_date AS orderDate,
	 customer AS customer,
	 money AS money,
	 pay_type AS payType,
	 real_money AS realMoney,
	 order_type AS orderType,
	 user_id AS userId
FROM ls_order
WHERE 1=1 

	 and order_date= #{orderDate}


	 and customer= #{customer}


	 and money= #{money}


	 and pay_type= #{payType}


	 and real_money= #{realMoney}


	 and order_type= #{orderType}


	 and user_id= #{userId}


	 and id= #{id}
